import React, { useState, useEffect, useCallback, useRef } from 'react';
import { useAuth } from '../../context/AuthContext';
import MapLoader      from './MapLoader';
import ControlPanel   from './ControlPanel';
import Sidebar        from './Sidebar';
import useMapData     from './hooks/useMapData';
import './Map.css';

export default React.forwardRef(function Map({ city, country, onResult }, ref) {
  const { user, isAdmin } = useAuth();
  const { weather, airQuality, feedbacks, markers, fetchData } = useMapData(city, country, user);

  const [center, setCenter] = useState({ lat: 28.6139, lng: 77.209 });
  const [zoom, setZoom]     = useState(city ? 13 : 6);
  const [showTraffic, setShowTraffic]         = useState(false);
  const [activeSidebar, setActiveSidebar]     = useState(null);
  const [showFeedbackForm, setShowFeedbackForm] = useState(false);
  const [selectedLocation, setSelectedLocation] = useState(null);

  const mapRef = useRef();

  // Handlers
  const handleReportClick = () => {
    if (!user) return alert('Please login to submit feedback');
    const c = mapRef.current.getCenter();
    setSelectedLocation(`${city} (${c.lat().toFixed(4)}, ${c.lng().toFixed(4)})`);
    setShowFeedbackForm(true);
  };

  const handleFeedbackSubmit = async data => {
    // same as original Map.js handleFeedbackSubmit :contentReference[oaicite:20]{index=20}&#8203;:contentReference[oaicite:21]{index=21}...
  };

  const getMarkerIcon = (type, severity) => {
    // copy from original Map.js :contentReference[oaicite:22]{index=22}&#8203;:contentReference[oaicite:23]{index=23}
  };

  const onToggle = name => {
    const next = activeSidebar === name ? null : name;
    setActiveSidebar(next);
    setShowTraffic(next === 'traffic');
    setShowFeedbackForm(false);
  };

  const onMarkerClick = id => {
    const fb = feedbacks.find(f => f.id === id);
    if (fb) {
      setSelectedLocation(fb.location || fb.location_name);
      setShowFeedbackForm(true);
      setActiveSidebar('feedback');
    }
  };

  const onLoad = map => {
    mapRef.current = map;
    map.addListener('click', e => {
      const loc = { lat: e.latLng.lat(), lng: e.latLng.lng() };
      setSelectedLocation(`${city} (${loc.lat.toFixed(4)}, ${loc.lng.toFixed(4)})`);
      setShowFeedbackForm(true);
    });
  };

  const onUnmount = () => { mapRef.current = null; };

  // Effect: when script loads & city/country changes
  useEffect(() => {
    fetchData().then(({ center: ctr, zoom: zm }) => {
      setCenter(ctr);
      setZoom(zm);
      onResult?.({ success: true, message: city, mapRef: { refocus: () => {
        setCenter(ctr); setZoom(zm); mapRef.current?.panTo(ctr);
      } } });
    }).catch(err => {
      onResult?.({ success: false, message: err.message });
    });
  }, [city, country, fetchData, onResult]);

  return (
    <div className="map-page-container">
      <ControlPanel active={activeSidebar} onToggle={onToggle} isAdmin={isAdmin} />

      <div className={`map-container ${activeSidebar ? 'sidebar-open' : ''}`}>
        <MapLoader
          center={center}
          zoom={zoom}
          showTraffic={showTraffic}
          markers={markers}
          getMarkerIcon={getMarkerIcon}
          onLoad={onLoad}
          onUnmount={onUnmount}
          onMapClick={() => {}}
          onMarkerClick={onMarkerClick}
        />
      </div>

      <Sidebar
        active={activeSidebar}
        onToggle={onToggle}
        weather={weather}
        airQuality={airQuality}
        feedbacks={feedbacks}
        markers={markers}
        city={city}
        isAdmin={isAdmin}
        showFeedbackForm={showFeedbackForm}
        selectedLocation={selectedLocation}
        onReportClick={handleReportClick}
        onFeedbackSubmit={handleFeedbackSubmit}
      />
    </div>
  );
});
