import React, { useEffect, useState } from 'react';
import api from '../../services/api';
import './EmergencyBannerX.css';

console.log('âœ… EmergencyBanner loaded');

const EmergencyBanner = () => {
  const [alert, setAlert] = useState(null);

  useEffect(() => {
    console.log('ðŸŸ¢ useEffect in EmergencyBanner fired');

    const fetchAlert = async () => {
      try {
        const response = await api.get('/api/alerts/');
        console.log('ðŸš¨ ALERT RESPONSE:', response.data);
        if (response.data.length > 0) {
          setAlert(response.data[0]);
        }
      } catch (err) {
        console.error('Error fetching alert:', err);
      }
    };

    fetchAlert();
  }, []);

  if (!alert) return null;
  console.log('ðŸ“¢ alert state:', alert);
  console.log('ðŸ“¢ returning banner');

  return (
    <div className={`emergency-banner ${alert.level}`}>
      <strong>{alert.title}:</strong> {alert.message}
    </div>
  );
};

export default EmergencyBanner;
