import React, { useState, useEffect, useCallback } from 'react';
import { GoogleMap, useLoadScript, TrafficLayer } from '@react-google-maps/api';
import { getWeatherData, getCityCoordinates } from '../services/weather';

const mapContainerStyle = {
  width: '100%',
  height: '400px',
};

// Define traffic regions
const trafficRegions = [
  {
    bounds: {
      north: 34.1,
      south: 34.0,
      east: -118.2,
      west: -118.3,
    },
    status: 'High',  // High traffic in this region
  },
  {
    bounds: {
      north: 34.2,
      south: 34.1,
      east: -118.1,
      west: -118.2,
    },
    status: 'Medium',  // Medium traffic in this region
  },
  {
    bounds: {
      north: 34.3,
      south: 34.2,
      east: -118.0,
      west: -118.1,
    },
    status: 'Low',  // Low traffic in this region
  },
];

const Map = ({ city }) => {
  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY,
  });

  const [weather, setWeather] = useState({
    main: { temp: 'N/A' },
    weather: [{ description: 'N/A' }],
  });
  const [trafficStatus, setTrafficStatus] = useState('Loading traffic data...');
  const [center, setCenter] = useState({
    lat: 34.04924594193164,  // Default center (Los Angeles)
    lng: -118.24104309082031,
  });
  const [map, setMap] = useState(null);

  // Fetch city coordinates and update map center
  useEffect(() => {
    const fetchCityData = async () => {
      try {
        const { lat, lon } = await getCityCoordinates(city);
        setCenter({ lat, lng: lon });

        // Fetch weather data for the new city
        const weatherData = await getWeatherData(lat, lon);
        setWeather(weatherData);
      } catch (error) {
        console.error('Error fetching city data:', error);
        setWeather({ error: 'Failed to load city data' });
      }
    };

    fetchCityData();
  }, [city]);

  // Handle map load
  const onLoad = useCallback((map) => {
    setMap(map);
  }, []);

  // Handle map idle event (when panning or zooming stops)
  const onIdle = useCallback(() => {
    if (map) {
      const bounds = map.getBounds();
      const ne = bounds.getNorthEast();
      const sw = bounds.getSouthWest();

      // Check which traffic region is visible
      const visibleRegion = trafficRegions.find((region) => {
        return (
          ne.lat() <= region.bounds.north &&
          sw.lat() >= region.bounds.south &&
          ne.lng() <= region.bounds.east &&
          sw.lng() >= region.bounds.west
        );
      });

      // Update traffic status
      if (visibleRegion) {
        setTrafficStatus(`Traffic: ${visibleRegion.status}`);
      } else {
        setTrafficStatus('Traffic: Unknown');
      }
    }
  }, [map]);

  // Handle errors and loading states
  if (loadError) return <div>Error loading maps</div>;
  if (!isLoaded) return <div>Loading Maps...</div>;

  return (
    <div>
      <div className="widgets">
        <div className="widget">
          <h2>Weather</h2>
          <p>
            {weather && weather.main
              ? `${weather.main.temp}°C, ${weather.weather[0].description}`
              : weather?.error || 'Loading weather data...'}
          </p>
        </div>

        <div className="widget">
          <h2>Traffic Status</h2>
          <p>{trafficStatus}</p>
        </div>
      </div>

      <GoogleMap
        mapContainerStyle={mapContainerStyle}
        zoom={13}
        center={center}
        onLoad={onLoad}  // Call onLoad when the map loads
        onIdle={onIdle}   // Call onIdle when the map becomes idle
      >
        <TrafficLayer />
      </GoogleMap>
    </div>
  );
};

export default Map;
