import React, { useState } from 'react';
import Map from './components/Map';
import './App.css';
import { getCountryCapital } from './services/countryData';

function App() {
  const [notification, setNotification] = useState(null);
  const [location, setLocation] = useState({
    city: 'Delhi',
    country: 'India'
  });
  const [searchQuery, setSearchQuery] = useState({
    city: '',
    country: ''
  });
  const [isLoading, setIsLoading] = useState(false);

  const handleSearch = async (e) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      // Trim and validate inputs
      const cityQuery = searchQuery.city.trim();
      const countryQuery = searchQuery.country.trim();

      if (!cityQuery && !countryQuery) {
        throw new Error('Please enter a city or country');
      }

      if (countryQuery && !cityQuery) {
        const capital = await getCountryCapital(countryQuery);
        setLocation({
          city: capital,
          country: countryQuery
        });
      } else {
        setLocation({
          city: cityQuery,
          country: countryQuery
        });
      }
    } catch (error) {
      alert(`Search error: ${error.message}`);
    } finally {
      setIsLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setSearchQuery(prev => ({
      ...prev,
      [name]: value
    }));
  };

  return (
    <div className="App">
      <h1>Smart City Dashboard</h1>

      {/* Search Bar */}
      <form onSubmit={handleSearch} className="search-bar">
      <div className="search-fields">
          <input
            type="text"
            name="city"
            placeholder="City (e.g. Delhi)"
            value={searchQuery.city}
            onChange={handleInputChange}
          />
          <input
            type="text"
            name="country"
            placeholder="Country (e.g. India)"
            value={searchQuery.country}
            onChange={handleInputChange}
          />
          <button type="submit" disabled={isLoading}>
            {isLoading ? 'Searching...' : 'Search'}
          </button>
        </div>
      </form>

      {/* Notification positioned below search bar */}
      <div className="notification-container">
        {notification && (
          <div className={`notification-banner ${notification.type}`}>
            <span>{notification.message}</span>
            <button onClick={() => setNotification(null)}>Ã—</button>
          </div>
        )}
      </div>

      {/* Map Component */}
      <Map city={location.city} country={location.country} />
    </div>
  );
}

export default App;
