import React, { useState, useEffect } from 'react';
import { GoogleMap, useLoadScript, TrafficLayer } from '@react-google-maps/api';
import { getWeatherData } from '../services/weather';

const mapContainerStyle = {
  width: '100%',
  height: '400px',
};

const center = {
  lat: 34.04924594193164,  // Default center (Los Angeles)
  lng: -118.24104309082031,
};

const Map = () => {
  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY,
  });

  const [weather, setWeather] = useState(null);
  const [trafficStatus, setTrafficStatus] = useState('Loading traffic data...');

  // Fetch weather data
  useEffect(() => {
    const fetchWeather = async () => {
      try {
        const data = await getWeatherData(center.lat, center.lng);
        console.log('Weather Data:', data);  // Log the response
        setWeather(data);
      } catch (error) {
        console.error('Error fetching weather data:', error);
        setWeather({ error: 'Failed to load weather data' });
      }
    };
  
    fetchWeather();
  }, []);
  

  // Simulate traffic status
  const getTrafficStatus = (map) => {
    const trafficLevels = ['Low', 'Medium', 'High'];
    const randomIndex = Math.floor(Math.random() * trafficLevels.length);
    return trafficLevels[randomIndex];
  };

  // Set traffic status when the map loads
  const onLoad = (map) => {
    console.log('Map loaded');  // Log when the map loads
    const status = getTrafficStatus(map);
    setTrafficStatus(`Traffic: ${status}`);
  };

  // Handle errors and loading states
  if (loadError) return <div>Error loading maps</div>;
  if (!isLoaded) return <div>Loading Maps...</div>;

  return (
    <div>
      <div className="widgets">
        <div className="widget">
          <h2>Weather</h2>
          <p>
            {weather && weather.main
              ? `${weather.main.temp}°C, ${weather.weather[0].description}`
              : weather?.error || 'Loading weather data...'}
          </p>
        </div>

        <div className="widget">
          <h2>Traffic Status</h2>
          <p>{trafficStatus}</p>
        </div>
      </div>

      <GoogleMap
        mapContainerStyle={mapContainerStyle}
        zoom={13}
        center={center}
        onLoad={onLoad}  // Call onLoad when the map loads
      >
        <TrafficLayer />
      </GoogleMap>
    </div>
  );
};

export default Map;
