import React, { useState, useEffect, useCallback } from 'react';
import { GoogleMap, useLoadScript, TrafficLayer } from '@react-google-maps/api';
import { getWeatherData, getCityCoordinates } from '../services/weather';
import './Map.css';

const Map = ({ city, country }) => {
  // State declarations
  const [weather, setWeather] = useState({
    main: { temp: 'N/A' },
    weather: [{ description: 'N/A' }],
  });
  const [center, setCenter] = useState({  // Added setCenter state
    lat: 34.04924594193164,
    lng: -118.24104309082031,
  });
  const [map, setMap] = useState(null);  // Added setMap state
  const [showTraffic, setShowTraffic] = useState(false);
  const [notification, setNotification] = useState(null);

  // Google Maps script loading
  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY,
  });

  // Fetch city coordinates
  useEffect(() => {
    const fetchCityData = async () => {
      try {
        setNotification(null);
        const result = await getCityCoordinates(city, country);
        
        if (country && !result.foundPrecise) {
          if (result.actualCountry) {
            setNotification({
              type: 'info',
              message: `Couldn't find ${city} in ${country}. Showing ${city}, ${result.actualCountry} instead.`
            });
          } else {
            setNotification({
              type: 'info', 
              message: `Couldn't find ${city} in ${country}. Showing ${city} globally.`
            });
          }
        }
        
        setCenter({ lat: result.lat, lng: result.lon });
        const weatherData = await getWeatherData(result.lat, result.lon);
        setWeather(weatherData);
        
      } catch (error) {
        if (error.message === 'LOCATION_NOT_FOUND') {
          setNotification({
            type: 'error',
            message: `Could not find ${city}${country ? ` in ${country}` : ''}. Please try another location.`
          });
        } else {
          setNotification({
            type: 'error',
            message: 'Failed to load weather data. Please try again later.'
          });
        }
        setWeather({
          error: true,
          main: { temp: 'N/A' },
          weather: [{ description: 'Error' }]
        });
      }
    };

    if (city) fetchCityData();
  }, [city, country]);

  // Handle map load
  const onLoad = useCallback((map) => {
    setMap(map);
  }, []);

  // Handle errors and loading states
  if (loadError) return <div>Error loading maps</div>;
  if (!isLoaded) return <div>Loading Maps...</div>;

  return (
    <div className="map-page-container">
      {/* Notification Banner */}
      {notification && (
        <div className={`notification-banner ${notification.type}`}>
          {notification.message}
          <button onClick={() => setNotification(null)}>×</button>
        </div>
      )}

      {/* Map Container (Left Side) */}
      <div className="map-container">
        <GoogleMap
          mapContainerStyle={{ width: '100%', height: '100%' }}
          zoom={13}
          center={center}
          onLoad={onLoad}
        >
          {showTraffic && <TrafficLayer />}
        </GoogleMap>
        {/* Traffic Toggle Button */}
        <button
          onClick={() => setShowTraffic(!showTraffic)}
          className="traffic-toggle"
        >
          {showTraffic ? 'Hide Traffic' : 'Show Traffic'}
        </button>
      </div>

      {/* Weather Widget (Right Side) */}
      <div className="weather-widget">
        <h2>Weather</h2>
        <p>
          {weather && weather.main
            ? `${weather.main.temp}°C, ${weather.weather[0].description}`
            : weather?.error || 'Loading weather data...'}
        </p>
      </div>
    </div>
  );
};

export default Map;
