import { createContext, useContext, useState, useEffect } from 'react';
import axios from 'axios';

const AuthContext = createContext();

axios.interceptors.request.use(config => {
  const token = localStorage.getItem('token');
  if (token) {
    config.headers.Authorization = `Token ${token}`;
  }
  return config;
});

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);  // user = full user object (not just email)
  const [isAdmin, setIsAdmin] = useState(false);
  const [loading, setLoading] = useState(true);

  const checkAuthStatus = async () => {
    try {
      const response = await axios.get(
        `${process.env.REACT_APP_API_BASE_URL}/api/auth/status/`,
        { withCredentials: true }
      );
      // Set full user object
      setUser({
        email: response.data.email,
        name: response.data.name || null  // support name later if needed
      });
      setIsAdmin(response.data.is_admin);
    } catch (error) {
      if (error.response?.status === 401) {
        localStorage.removeItem('token');
      }
      setUser(null);
      setIsAdmin(false);
    } finally {
      setLoading(false);
    }
  };

  const login = (token, isAdminLogin = false) => {
    localStorage.setItem('token', token);
    checkAuthStatus();
  };

  const logout = () => {
    const email = user?.email;
    if (email) {
      localStorage.removeItem(`upvotedFeedbacks_${email}`);
    }
    localStorage.removeItem('token');
    setUser(null);
    setIsAdmin(false);
  };

  return (
    <AuthContext.Provider value={{
      user,
      isAdmin,
      loading,
      login,
      logout,
      checkAuthStatus
    }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);
