import React, { useState } from 'react';
import './Feedback.css';

const FeedbackItem = ({ feedback }) => {
  const [upvoted, setUpvoted] = useState(false);
  const [upvoteCount, setUpvoteCount] = useState(feedback.upvotes || 0);

  const handleUpvote = () => {
    if (!upvoted) {
      setUpvoteCount(upvoteCount + 1);
      setUpvoted(true);
      // Here you would also update the backend
    }
  };

  const getIcon = (type) => {
    switch(type) {
      case 'pothole': return 'üï≥Ô∏è';
      case 'streetlight': return 'üí°';
      case 'intersection': return 'üö¶';
      case 'garbage': return 'üóëÔ∏è';
      case 'water': return 'üíß';
      default: return '‚ùó';
    }
  };

  const getStatusColor = (status) => {
    switch(status) {
      case 'reported': return 'gray';
      case 'in_progress': return 'blue';
      case 'resolved': return 'green';
      default: return 'gray';
    }
  };

  return (
    <div className="feedback-item">
      <div className="feedback-header">
        <span className="feedback-icon">{getIcon(feedback.issueType)}</span>
        <span className="feedback-type">{feedback.issueType.replace(/_/g, ' ')}</span>
        <span 
          className="feedback-status" 
          style={{ backgroundColor: getStatusColor(feedback.status) }}
        >
          {feedback.status.replace(/_/g, ' ')}
        </span>
      </div>
      <div className="feedback-description">{feedback.description}</div>
      
      <div className="feedback-meta">
        <button 
          className={`upvote-btn ${upvoted ? 'upvoted' : ''}`}
          onClick={handleUpvote}
          disabled={upvoted}
        >
          üëç {upvoteCount}
        </button>
        
        <div className="feedback-location-date">
          <span className="feedback-location">{feedback.location}</span>
          <span className="feedback-date">
            {new Date(feedback.timestamp).toLocaleString()}
          </span>
        </div>
      </div>
    </div>
  );
};

export default FeedbackItem;
