import React, { useState } from 'react';
import './Feedback.css';
import { useAuth } from '../../context/AuthContext';




const [formData, setFormData] = useState({
  issueType: 'pothole',
  description: '',
  severity: 'medium',
  isAnonymous: false,
});


  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const { user } = useAuth();

  const handleSubmit = (e) => {
    e.preventDefault(); // prevent full page reload
  
    if (!user) {
      alert('Please login to submit feedback');
      return;
    }
  
    const feedbackData = {
      issue_type: formData.issueType,
      description: formData.description,
      severity: formData.severity,
      location_name: location.name || location.address || "Unknown location",
      latitude: location.lat,
      longitude: location.lng,
      is_anonymous: formData.isAnonymous,
      status: "reported"
    };
    
    console.log("Submitting feedback:", feedbackData); //for debug
  
    onSubmit(feedbackData);

  return (
    <div className="feedback-form-container">
      <h3>Report an Issue</h3>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Issue Type:</label>
          <select 
            name="issueType" 
            value={formData.issueType} 
            onChange={handleChange}
            required
          >
            <option value="pothole">Pothole</option>
            <option value="streetlight">Damaged Streetlight</option>
            <option value="intersection">Unsafe Intersection</option>
            <option value="garbage">Garbage Accumulation</option>
            <option value="water">Water Logging</option>
            <option value="other">Other</option>
          </select>
        </div>

        <div className="form-group">
          <label>Description:</label>
          <textarea 
            name="description" 
            value={formData.description} 
            onChange={handleChange}
            placeholder="Please provide detailed information about the issue..."
            required
          />
        </div>

        <div className="form-group">
          <label>Severity:</label>
          <select name="severity" value={formData.severity} onChange={handleChange} required>
            <option value="low">Low</option>
            <option value="medium">Medium</option>
            <option value="high">High</option>
          </select>
        </div>

        <div className="form-group">
          <p className="location-display">
            <strong>Location:</strong> {location}
          </p>
        </div>

        <div className="form-actions">
          <button type="button" className="cancel-btn" onClick={onCancel}>
            Cancel
          </button>
          <button type="submit" className="submit-btn">Submit Report</button>
        </div>
      </form>
    </div>
  );

};

export default FeedbackForm;
