import React, { useState, useEffect, useCallback, useRef } from 'react';
import { GoogleMap, useLoadScript, TrafficLayer } from '@react-google-maps/api';
import { getWeatherData, getCityCoordinates } from '../services/weather';
import { GOOGLE_MAPS_CONFIG } from './configs/maps';
import './Map.css';

const Map = ({ city, country, onResult }) => {
  const [weather, setWeather] = useState(null);
  const [airQuality, setAirQuality] = useState(null);
  const [center, setCenter] = useState({ lat: 28.6139, lng: 77.2090 });
  const [map, setMap] = useState(null);
  const [showTraffic, setShowTraffic] = useState(false);
  const [activeSidebar, setActiveSidebar] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [mapPadding, setMapPadding] = useState({ left: 220, right: 0 });
  const prevSearchRef = useRef('');
  const mapRef = useRef(null);

  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: GOOGLE_MAPS_CONFIG.apiKey,
    libraries: GOOGLE_MAPS_CONFIG.libraries,
  });

  const fetchData = useCallback(async () => {
    try {
      const currentSearch = `${city}|${country}`;
      if (prevSearchRef.current === currentSearch || (!city && !country)) {
        return;
      }
      prevSearchRef.current = currentSearch;

      setIsLoading(true);
      setWeather(null);
      setAirQuality(null);

      const { lat, lon } = await getCityCoordinates(city, country);
      setCenter({ lat, lng: lon });

      const [weatherData, airQualityData] = await Promise.all([
        getWeatherData(lat, lon),
        getAirQualityData(lat, lon)
      ]);

      setWeather(weatherData);
      setAirQuality(airQualityData);

      if (onResult) {
        const message = city 
          ? `${city} found${country ? ` in ${country}` : ''}`
          : `Showing capital of ${country}`;
        onResult({ success: true, message });
      }

    } catch (error) {
      console.error('Error:', error);
      if (onResult) {
        let message;
        switch(error.message) {
          case 'CITY_NOT_FOUND':
            message = `${city} not found${country ? ` in ${country}` : ''}`;
            break;
          case 'CITY_NOT_IN_COUNTRY':
            message = `${city} not found in ${country}`;
            break;
          case 'COUNTRY_NOT_FOUND':
            message = `${country} not found`;
            break;
          case 'INVALID_COUNTRY':
            message = `Invalid country: ${country}`;
            break;
          default:
            message = 'Failed to load location data';
        }
        onResult({ success: false, message });
      }
    } finally {
      setIsLoading(false);
    }
  }, [city, country, onResult]);

  useEffect(() => {
    if (!isLoaded) return;
    fetchData();
  }, [isLoaded, fetchData]);

  const onLoad = useCallback((map) => {
    mapRef.current = map;
    setMap(map);
  }, []);

  const onUnmount = useCallback(() => {
    mapRef.current = null;
  }, []);

  const toggleSidebar = (sidebarName) => {
    if (activeSidebar === sidebarName) {
      setActiveSidebar(null);
      setMapPadding({ left: 220, right: 0 });
      setShowTraffic(false);
    } else {
      setActiveSidebar(sidebarName);
      setMapPadding({ left: 220, right: 300 });
      setShowTraffic(sidebarName === 'traffic');
    }
    setTimeout(() => {
      if (mapRef.current) {
        window.dispatchEvent(new Event('resize'));
        mapRef.current.panTo(center);
      }
    }, 300);
  };

  if (loadError) return <div className="map-error">Error loading maps</div>;
  if (!isLoaded) return <div className="map-loading">Loading Maps...</div>;

  return (
    <div className="map-page-container">
      <div className="left-panel">
        <div 
          className={`control-card ${activeSidebar === 'weather' ? 'active' : ''}`} 
          onClick={() => toggleSidebar('weather')}
        >
          <div className="control-icon">☀️</div>
          <div className="control-label">Weather</div>
        </div>
        <div 
          className={`control-card ${activeSidebar === 'airQuality' ? 'active' : ''}`} 
          onClick={() => toggleSidebar('airQuality')}
        >
          <div className="control-icon">🌫️</div>
          <div className="control-label">Air Quality</div>
        </div>
        <div 
          className={`control-card ${activeSidebar === 'traffic' ? 'active' : ''}`} 
          onClick={() => toggleSidebar('traffic')}
        >
          <div className="control-icon">🚦</div>
          <div className="control-label">Traffic</div>
        </div>
      </div>

      <div className="map-container">
        <GoogleMap
          mapContainerStyle={{ 
            width: '100%', 
            height: '100%',
            paddingLeft: `${mapPadding.left}px`,
            paddingRight: `${mapPadding.right}px`,
            transition: 'padding 0.3s ease'
          }}
          zoom={13}
          center={center}
          onLoad={onLoad}
          onUnmount={onUnmount}
          options={{
            gestureHandling: 'greedy',
            disableDefaultUI: true,
            styles: [
              {
                featureType: "poi",
                elementType: "labels",
                stylers: [{ visibility: "off" }]
              }
            ]
          }}
        >
          {showTraffic && <TrafficLayer />}
        </GoogleMap>
      </div>

      {activeSidebar === 'weather' && weather && (
        <div className="right-sidebar">
          <div className="sidebar-header">
            <h2>Weather Data</h2>
            <button onClick={() => toggleSidebar('weather')} className="close-sidebar">
              &times;
            </button>
          </div>
          <div className="sidebar-content">
            <div className="weather-display">
              <div className="weather-icon">
                <img 
                  src={`https://openweathermap.org/img/wn/${weather.weather[0].icon}@4x.png`} 
                  alt={weather.weather[0].description}
                />
              </div>
              <div className="weather-details">
                <div className="temperature">{Math.round(weather.main.temp)}°C</div>
                <div className="description">{weather.weather[0].description}</div>
              </div>
            </div>
            <div className="weather-stats">
              <div className="stat-item">
                <span>Feels Like</span>
                <strong>{Math.round(weather.main.feels_like)}°C</strong>
              </div>
              <div className="stat-item">
                <span>Humidity</span>
                <strong>{weather.main.humidity}%</strong>
              </div>
              <div className="stat-item">
                <span>Wind</span>
                <strong>{weather.wind.speed} m/s</strong>
              </div>
              <div className="stat-item">
                <span>Pressure</span>
                <strong>{weather.main.pressure} hPa</strong>
              </div>
            </div>
          </div>
        </div>
      )}

{activeSidebar === 'airQuality' && airQuality && (
        <div className="right-sidebar">
          <div className="sidebar-header">
            <h2>Air Quality Index</h2>
            <button onClick={() => toggleSidebar('airQuality')} className="close-sidebar">
              &times;
            </button>
          </div>
          <div className="sidebar-content">
            <div className="aqi-display">
              <div className={`aqi-value aqi-${getAqiLevel(airQuality.list[0].main.aqi)}`}>
                {airQuality.list[0].main.aqi}
              </div>
              <div className="aqi-label">
                {getAqiDescription(airQuality.list[0].main.aqi)}
              </div>
            </div>
            <div className="pollution-stats">
              <div className="stat-item">
                <span>PM2.5</span>
                <strong>{airQuality.list[0].components.pm2_5} μg/m³</strong>
              </div>
              <div className="stat-item">
                <span>PM10</span>
                <strong>{airQuality.list[0].components.pm10} μg/m³</strong>
              </div>
              <div className="stat-item">
                <span>NO₂</span>
                <strong>{airQuality.list[0].components.no2} μg/m³</strong>
              </div>
              <div className="stat-item">
                <span>O₃</span>
                <strong>{airQuality.list[0].components.o3} μg/m³</strong>
              </div>
            </div>
          </div>
        </div>
      )}

      {activeSidebar === 'traffic' && (
        <div className="right-sidebar">
          <div className="sidebar-header">
            <h2>Traffic Data</h2>
            <button onClick={() => toggleSidebar('traffic')} className="close-sidebar">
              &times;
            </button>
          </div>
          <div className="sidebar-content">
            <div className="traffic-info">
              <p>Traffic layer is currently {showTraffic ? 'enabled' : 'disabled'} on the map.</p>
              <div className="traffic-legend">
                <div className="legend-item">
                  <span className="traffic-green"></span>
                  <span>Flowing</span>
                </div>
                <div className="legend-item">
                  <span className="traffic-yellow"></span>
                  <span>Slow</span>
                </div>
                <div className="legend-item">
                  <span className="traffic-red"></span>
                  <span>Congested</span>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

// Helper functions for air quality
function getAqiLevel(aqi) {
  if (aqi <= 50) return 'good';
  if (aqi <= 100) return 'moderate';
  if (aqi <= 150) return 'unhealthy-sensitive';
  if (aqi <= 200) return 'unhealthy';
  if (aqi <= 300) return 'very-unhealthy';
  return 'hazardous';
}

function getAqiDescription(aqi) {
  const levels = {
    good: 'Good',
    moderate: 'Moderate',
    'unhealthy-sensitive': 'Unhealthy for Sensitive Groups',
    unhealthy: 'Unhealthy',
    'very-unhealthy': 'Very Unhealthy',
    hazardous: 'Hazardous'
  };
  return levels[getAqiLevel(aqi)];
}

export default Map;
