import React from 'react';
import AdminSidebar      from './AdminSidebar';
import WeatherSidebar    from './WeatherSidebar';
import AirQualitySidebar from './AirQualitySidebar';
import TrafficSidebar    from './TrafficSidebar';
import FeedbackSidebar   from './FeedbackSidebar';

const titles = {
  admin:      'Admin Dashboard',
  weather:    'Weather Data',
  airQuality: 'Air Quality Data',
  traffic:    'Traffic Data',
  feedback:   'City Issues & Reports'
};

export default function Sidebar(props) {
  const { active } = props;
  if (!active) return null;

  const panels = {
    admin:      <AdminSidebar {...props} />,
    weather:    <WeatherSidebar {...props} />,
    airQuality: <AirQualitySidebar {...props} />,
    traffic:    <TrafficSidebar {...props} />,
    feedback:   <FeedbackSidebar {...props} />
  };

  return (
    <div className={`right-sidebar active`}>
      <div className="sidebar-header">
        <h2>{titles[active]}</h2>
        <button onClick={() => props.onToggle(active)} className="close-sidebar">&times;</button>
      </div>
      <div className="sidebar-content">
        {panels[active]}
      </div>
    </div>
  );
}
