// src/components/Map/index.js
import React, { useState, useEffect, useRef } from 'react';
import { useAuth } from '../../context/AuthContext';
import MapLoader from './MapLoader';
import ControlPanel from './ControlPanel';
import Sidebar from './Sidebar';
import useMapData from './hooks/useMapData';
import useAccidentZones from './hooks/useAccidentZones';
import useAlerts from './hooks/useAlerts';
import './Map.css';

export default React.forwardRef(function Map({ city, country, onResult }, ref) {
  const { user } = useAuth();
  const { weather, airQuality, feedbacks, markers, fetchData, addFeedback } =
    useMapData(city, country, user);
  const { zones: accidentZones } = useAccidentZones(city);
  const { alerts } = useAlerts(city);

  const [center, setCenter] = useState(null);
  const [zoom, setZoom] = useState(city ? 13 : 6);
  const [showTraffic, setShowTraffic] = useState(false);
  const [showZones, setShowZones] = useState(false);
  const [showAlerts, setShowAlerts] = useState(false);
  const [activeSidebar, setActiveSidebar] = useState(null);
  const [showFeedbackForm, setShowFeedbackForm] = useState(false);
  const [selectedLocation, setSelectedLocation] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const mapRef = useRef();
  const currentLocationRef = useRef({ city: 'New Delhi', country: 'India' });
  const hasGeolocated = useRef(false);
  const firstSearchApplied = useRef(false);

  // Handle user geolocation on every load
  useEffect(() => {
    // Only attempt geolocation if the city is still the default
    if (!hasGeolocated.current && city === 'New Delhi') {
      navigator.geolocation.getCurrentPosition(
        async pos => {
          const userLat = pos.coords.latitude;
          const userLng = pos.coords.longitude;
          console.log('ðŸ“ Using geolocation:', userLat, userLng);
          setCenter({ lat: userLat, lng: userLng });
          setZoom(13);
          hasGeolocated.current = true;
  
          // Optional: Reverse geocode to get city name from lat/lng
          try {
            const response = await fetch(
              `https://maps.googleapis.com/maps/api/geocode/json?latlng=${userLat},${userLng}&key=YOUR_GOOGLE_MAPS_API_KEY`
            );
            const data = await response.json();
            const address = data.results?.[0]?.address_components || [];
            const cityComponent = address.find(comp => comp.types.includes('locality'));
            const countryComponent = address.find(comp => comp.types.includes('country'));
            const cityName = cityComponent?.long_name || 'Unknown';
            const countryName = countryComponent?.long_name || 'Unknown';
  
            currentLocationRef.current = { city: cityName, country: countryName };
            onResult?.({
              success: true,
              message: `Showing location: ${cityName}`,
              mapRef: {
                refocus: () => {
                  mapRef.current?.panTo({ lat: userLat, lng: userLng });
                  mapRef.current?.setZoom(13);
                }
              }
            });
          } catch (e) {
            console.warn('Reverse geocoding failed:', e);
          }
        },
        err => {
          console.warn('âš ï¸ Geolocation failed, fallback to New Delhi:', err.message);
          setCenter({ lat: 28.6139, lng: 77.2090 }); // New Delhi fallback
          setZoom(13);
          hasGeolocated.current = true;
        }
      );
    }
  }, [city, country, onResult]);
  

  // Reset UI when city changes
  useEffect(() => {
    currentLocationRef.current = { city, country };
    setActiveSidebar(null);
    setShowZones(false);
    setShowAlerts(false);
    setShowTraffic(false);
    setShowFeedbackForm(false);
    firstSearchApplied.current = false;
  }, [city, country]);

  // Sidebar toggler
  const onToggle = name => {
    const next = activeSidebar === name ? null : name;
    setActiveSidebar(next);
    setShowTraffic(next === 'traffic');
    setShowZones(next === 'zones');
    setShowAlerts(next === 'alerts');
    setShowFeedbackForm(false);
  };

  // Feedback report button
  const handleReportClick = () => {
    if (!user) return alert('Please login to submit feedback');
    const c = mapRef.current?.getCenter();
    if (!c) return;
    setSelectedLocation(
      `${currentLocationRef.current.city} (${c.lat().toFixed(4)}, ${c.lng().toFixed(4)})`
    );
    setShowFeedbackForm(true);
    setActiveSidebar('feedback');
  };

  // Submit feedback
  const handleFeedbackSubmit = async feedback => {
    if (!user) return alert('Please login to submit feedback');
    try {
      const c = mapRef.current?.getCenter();
      await addFeedback({
        ...feedback,
        id: `feedback-${Date.now()}`,
        location_name: selectedLocation,
        latitude: c.lat(),
        longitude: c.lng()
      });
      setShowFeedbackForm(false);
      alert('Thank you for your report!');
    } catch (err) {
      console.error(err);
      alert('Failed to submit feedback');
    }
  };

  // Marker click â†’ open feedback item
  const onMarkerClick = id => {
    const fb = feedbacks.find(f => f.id === id);
    if (!fb) return;
    setSelectedLocation(fb.location || fb.location_name);
    setShowFeedbackForm(true);
    setActiveSidebar('feedback');
  };

  // Map load handler
  const onLoad = map => {
    mapRef.current = map;
    map.addListener('click', e => {
      if (activeSidebar !== 'feedback') return;
      const lat = e.latLng.lat();
      const lng = e.latLng.lng();
      const label = currentLocationRef.current.city
        ? `${currentLocationRef.current.city} (${lat.toFixed(4)}, ${lng.toFixed(4)})`
        : `(${lat.toFixed(4)}, ${lng.toFixed(4)})`;
      setSelectedLocation(label);
      setShowFeedbackForm(true);
    });
  };

  const onUnmount = () => {
    mapRef.current = null;
  };

  // Fetch map data and center based on result
  useEffect(() => {
    if (!city) return;
    setIsLoading(true);
    fetchData()
      .then(result => {
        if (result.error) {
          onResult?.({ success: false, message: result.message });
          return;
        }
        if (result.center && result.zoom != null && !firstSearchApplied.current) {
          setCenter(result.center);
          setZoom(result.zoom);
          mapRef.current?.panTo(result.center);
          mapRef.current?.setZoom(result.zoom);
          firstSearchApplied.current = true;
        }

        onResult?.({
          success: true,
          message: city,
          mapRef: {
            refocus: () => {
              setCenter(result.center);
              setZoom(result.zoom);
              mapRef.current?.panTo(result.center);
              mapRef.current?.setZoom(result.zoom);
            }
          }
        });
      })
      .catch(err => {
        onResult?.({ success: false, message: 'Unexpected error: ' + err.message });
      })
      .finally(() => setIsLoading(false));
  }, [city, country, fetchData, onResult]);

  return (
    <div className="map-page-container">
      <ControlPanel active={activeSidebar} onToggle={onToggle} />

      <div className={`map-container ${activeSidebar ? 'sidebar-open' : ''}`}>
        {isLoading && (
          <div className="map-loading-overlay"><div className="spinner" /></div>
        )}
        {center && (
          <MapLoader
            center={center}
            zoom={zoom}
            showTraffic={showTraffic}
            showZones={showZones}
            showAlerts={showAlerts}
            accidentZones={accidentZones}
            alerts={alerts}
            markers={markers}
            onLoad={onLoad}
            onUnmount={onUnmount}
            onMarkerClick={onMarkerClick}
          />
        )}
      </div>

      <Sidebar
        active={activeSidebar}
        onToggle={onToggle}
        weather={weather}
        airQuality={airQuality}
        city={currentLocationRef.current.city}
        currentLocation={currentLocationRef.current}
        showFeedbackForm={showFeedbackForm}
        selectedLocation={selectedLocation}
        onReportClick={handleReportClick}
        onFeedbackSubmit={handleFeedbackSubmit}
        accidentZones={accidentZones}
        alerts={alerts}
      />
    </div>
  );
});
