import React, { useState, useEffect, useRef} from 'react';
import Map from './components/Map';
import './App.css';
import axios from 'axios';

function App() {
  const [location, setLocation] = useState({
    city: 'New Delhi',
    country: 'India'
  });
  const [searchQuery, setSearchQuery] = useState({
    city: '',
    country: ''
  });
  const [notification, setNotification] = useState(null);
  const [lastValidSearch, setLastValidSearch] = useState({
    city: 'New Delhi',
    country: 'India'
  });
  const [isAuthenticated, setIsAuthenticated] = useState(false); // Add this line
  const prevSearchRef = useRef('');
  const mapRef = useRef();

  const [userData, setUserData] = useState(null);

  // Check authentication status on load
  useEffect(() => {
    const token = localStorage.getItem('token');
    const userData = JSON.parse(localStorage.getItem('userData'));

    if (token && userData) {
      setIsAuthenticated(true);
      setUserData(userData);
      // Configure axios headers
      axios.defaults.headers.common['Authorization'] = `Token ${token}`;
    }

    // Listen for global auth events
    const handleUnauthorized = () => {
      localStorage.clear();
      setIsAuthenticated(false);
      setUserData(null);
    };

    window.addEventListener('unauthorized', handleUnauthorized);
    return () => window.removeEventListener('unauthorized', handleUnauthorized);
  }, []);

  // Auto-dismiss notification after 2 seconds
  useEffect(() => {
    if (notification) {
      const timer = setTimeout(() => {
        setNotification(null);
      }, 2000);
      return () => clearTimeout(timer);
    }
  }, [notification]);

  const handleLoginSuccess = (responseData) => {
    localStorage.setItem('token', responseData.token);  // Ensure token is stored
    localStorage.setItem('userData', JSON.stringify({
      username: responseData.username,
      email: responseData.email,
      name: responseData.name,
      mobile: responseData.mobile
    }));
    setIsAuthenticated(true);
    setUserData(responseData);
  };

  const handleLogout = async () => {
    try {
      const token = localStorage.getItem('token');  
      if (token) {
        await fetch('http://localhost:8000/api/auth/logout/', {
          method: 'POST',
          headers: {
            'Authorization': `Token ${token}`  
          }
        });
      }
    } catch (error) {
      console.error('Logout error:', error);
    } finally {
      localStorage.removeItem('token');  
      localStorage.removeItem('userData');
      setIsAuthenticated(false);
      setUserData(null);
    }
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    try {
      if (!searchQuery.city && !searchQuery.country) {
        throw new Error('Please enter a city or country');
      }

      const newLocation = {
        city: searchQuery.city.trim(),
        country: searchQuery.country.trim()
      };

      setLocation(newLocation);
      setLastValidSearch(newLocation);

      setNotification({
        type: 'info',
        message: 'Searching for location...'
      });

    } catch (error) {
      setNotification({
        type: 'error',
        message: error.message
      });
    }
  };

  const handleMapResult = (result) => {
    setNotification({
      type: result.success ? 'success' : 'error',
      message: result.message
    });
    if (result.mapRef) {
      mapRef.current = result.mapRef;
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setSearchQuery(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const refocusOnLastSearch = () => {
    if (mapRef.current && lastValidSearch) {
      mapRef.current.refocus();
      setNotification({
        type: 'info',
        message: `Refocusing on ${lastValidSearch.city || lastValidSearch.country}`
      });
    }
  };

  return (
    <div className="App">
      <div style={{ display: 'flex', justifyContent: 'center' }}>
        <h1>Smart City Dashboard</h1>
      </div>

      {notification && (
        <div className={`notification-banner ${notification.type}`}>
          <span>{notification.message}</span>
        </div>
      )}

      <form onSubmit={handleSearch} className="search-bar">
        <div className="search-container">
          <div className="search-controls">
            <button 
              type="button"
              className="refocus-button"
              onClick={refocusOnLastSearch}
              title="Refocus on last searched location"
            >
              <span className="refocus-icon">📍</span>
            </button>
            <input
              type="text"
              name="city"
              placeholder="City (e.g. Delhi)"
              value={searchQuery.city}
              onChange={handleInputChange}
            />
            <input
              type="text"
              name="country"
              placeholder="Country (e.g. India)"
              value={searchQuery.country}
              onChange={handleInputChange}
            />
            <button type="submit" className="search-button">Search</button>
          </div>
        </div>
      </form>

      <Map 
        city={location.city}
        country={location.country}
        onResult={handleMapResult}
        isAuthenticated={isAuthenticated}
        setIsAuthenticated={setIsAuthenticated}
        ref={mapRef}
        userData={userData}
        setUserData={setUserData}
        onLoginSuccess={handleLoginSuccess}
        onLogout={handleLogout}
      />
    </div>
  );
}

export default App;
