// src/components/Map/Sidebar/index.js
import React from 'react'
// ... other imports ...
import EmergencyAlertSidebar from './EmergencyAlertSidebar'

const titles = {
  // ... other titles ...
  alerts: 'Emergency Alerts',
}

export default function Sidebar({
  active,
  onToggle,
  accidentZones,
  alerts,
  ...rest
}) {
  return (
    <div className={`right-sidebar ${active ? 'active' : ''}`}>
      {active && (
        <>
          <div className="sidebar-header">
            <h2>{titles[active]}{active === 'alerts' ? ` – ${rest.city}` : ''}</h2>
            <button onClick={() => onToggle(active)} className="close-sidebar">&times;</button>
          </div>
          <div className="sidebar-content">
            {/* ... other panels ... */}
            {active === 'alerts' && (
              <EmergencyAlertSidebar alerts={alerts} city={rest.city} />
            )}
            {/* ... zones panel ... */}
          </div>
        </>
      )}
    </div>
  )
}
