import React, { useState, useEffect } from 'react';
import { getTrafficData, getWeatherData } from './services/api';
import Map from './components/Maps';
import './App.css';

function App() {
  const [traffic, setTraffic] = useState(null);
  const [weather, setWeather] = useState(null);

  useEffect(() => {
    // Fetch traffic data
    getTrafficData().then(data => {
      if (data.length > 0) setTraffic(data[0]);  // Use the latest entry
    });

    // Fetch weather data
    getWeatherData().then(data => {
      if (data.length > 0) setWeather(data[0]);  // Use the latest entry
    });

  }, []);

  return (
    <div className="App">
      <h1>Smart City Dashboard</h1>

      <div className="widgets">
        <div className="widget">
          <h2>Traffic Status</h2>
          <p>{traffic ? traffic.status : 'Loading traffic data...'}</p>
        </div>

        <div className="widget">
          <h2>Weather</h2>
          <p>
            {weather
              ? `${weather.temperature}°C, ${weather.humidity}% humidity`
              : 'Loading weather data...'}
          </p>
        </div>
        </div>

        <div className="map-container">
        <h2>City Map</h2>
        <Map />
      </div>
    </div>
  );
}

export default App;
