import React, { useState } from 'react';
import Map from './components/Map';
import './App.css';

function App() {
  const [location, setLocation] = useState({
    city: '',
    country: ''
  });
  const [searchQuery, setSearchQuery] = useState({
    city: '',
    country: ''
  });
  const [notification, setNotification] = useState(null);

  // Show notification with auto-dismiss
  const showNotification = (type, message, persist = false) => {
    setNotification({ type, message });
    if (!persist) {
      setTimeout(() => setNotification(null), 5000);
    }
  };

  // Handle search submission
  const handleSearch = async (e) => {
    e.preventDefault();

    try {
      // Validate input
      if (!searchQuery.city && !searchQuery.country) {
        throw new Error('Please enter a city or country');
      }

      showNotification('info', 'Searching for location...', true);

      // Update location state
      setLocation({
        city: searchQuery.city.trim(),
        country: searchQuery.country.trim()
      });

    } catch (error) {
      showNotification('error', error.message);
    }
  };

  // Handle map errors
  const handleMapError = (error) => {
    showNotification(error.type || 'error', error.message, error.persist);
  };

  // Handle input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setSearchQuery(prev => ({
      ...prev,
      [name]: value
    }));
  };

  return (
    <div className="App">
      <h1>Smart City Dashboard</h1>

      {/* Notification Container */}
      <div className="notification-container">
        {notification && (
          <div className={`notification-banner ${notification.type}`}>
            <span>{notification.message}</span>
            <button 
              onClick={() => setNotification(null)}
              aria-label="Close notification"
            >
              Ã—
            </button>
          </div>
        )}
      </div>

      {/* Search Form */}
      <form onSubmit={handleSearch} className="search-bar">
        <div className="search-fields">
          <input
            type="text"
            name="city"
            placeholder="City (e.g. Delhi)"
            value={searchQuery.city}
            onChange={handleInputChange}
            aria-label="City input"
          />
          <input
            type="text"
            name="country"
            placeholder="Country (e.g. India or IN)"
            value={searchQuery.country}
            onChange={handleInputChange}
            list="countries"
            aria-label="Country input"
          />
          <datalist id="countries">
            <option value="India (IN)"></option>
            <option value="United States (US)"></option>
            <option value="France (FR)"></option>
            <option value="Germany (DE)"></option>
            <option value="IN"></option>
            <option value="US"></option>
            <option value="FR"></option>
            <option value="DE"></option>
          </datalist>
          <button 
            type="submit"
            aria-label="Search location"
          >
            Search
          </button>
        </div>
      </form>

      {/* Map Component */}
      <Map 
        city={location.city} 
        country={location.country}
        onError={handleMapError}
      />
    </div>
  );
}

export default App;
