// src/components/Map/MapLoader.js
import React from 'react'
import {
  GoogleMap,
  useLoadScript,
  TrafficLayer,
  Polygon,
  Marker
} from '@react-google-maps/api'
import { GOOGLE_MAPS_CONFIG } from './configs/maps'

export default function MapLoader({
  center,
  zoom,
  showTraffic,
  showZones,
  showAlerts,
  accidentZones,
  alerts,
  onLoad,
  onUnmount
}) {
  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: GOOGLE_MAPS_CONFIG.apiKey,
    libraries: [GOOGLE_MAPS_CONFIG.libraries,'drawing']
  })

  if (loadError) return <div className="map-error">Error loading map</div>
  if (!isLoaded) return <div className="map-loading">Loading Mapâ€¦</div>

  return (
    <GoogleMap
      mapContainerStyle={{ width: '100%', height: '100%' }}
      center={center}
      zoom={zoom}
      onLoad={onLoad}
      onUnmount={onUnmount}
      options={{
        disableDefaultUI: false,
        gestureHandling: 'greedy',
        zoomControl: true,
        mapTypeControl: true,
        streetViewControl: true,
        fullscreenControl: true,
        styles: [{ featureType: "poi", elementType: "labels", stylers: [{ visibility: "off" }] }]
      }}
    >
      {showTraffic && <TrafficLayer />}

      {showZones &&
        accidentZones.map(z => (
          <Polygon
            key={z.id}
            paths={z.polygon.map(p => ({ lat: +p[0], lng: +p[1] }))}
            options={{
              fillColor: 'red',
              fillOpacity: 0.3,
              strokeColor: 'red',
              strokeOpacity: 0.8
            }}
          />
        ))}

      {showAlerts &&
        alerts.map(a => (
          <Marker
            key={a.id}
            position={{ lat: a.latitude, lng: a.longitude }}
            title={a.message}
            icon="https://maps.google.com/mapfiles/ms/icons/yellow-dot.png"
          />
        ))}
    </GoogleMap>
  )
}
