// src/components/Auth/GoogleLogin.js

import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google'
import axios from 'axios'
import { useAuth } from '../../context/AuthContext'
import { toast } from 'react-toastify'
import './GoogleLogin.css'

const GoogleAuth = () => {
  const { user, login, logout } = useAuth()

  const handleSuccess = async credentialResponse => {
    const id_token = credentialResponse.credential;
    console.log("üü¢ ID Token:", id_token);
  
    try {
      const { data } = await axios.post(
        `${process.env.REACT_APP_API_BASE_URL}/api/auth/google/`,
        { id_token },
        { withCredentials: true }
      )
      console.log("‚úÖ Backend response:", data)
      login(data.key, data.user)
      toast.success('‚úÖ Logged in successfully!')
    } catch (err) {
      console.error("‚ùå Backend error:", err.response?.data || err.message)
      toast.error(`‚ùå Login failed: ${err.response?.data?.error || err.message}`)
    }
  }

  return (
    <GoogleOAuthProvider clientId={process.env.REACT_APP_GOOGLE_CLIENT_ID}>
      {!user ? (
        <div className="google-auth-wrapper">
          <GoogleLogin
            onSuccess={handleSuccess}
            onError={() => toast.error('‚ùå Google Login Failed')}
            ux_mode="popup"
            theme="outline"
            size="large"
          />
        </div>
      ) : (
        <div className="google-auth-user-info">
          <span className="user-email">üë§ {user.email}</span>
          <button
            className="logout-btn"
            onClick={() => {
              logout()
              toast.info('üîì Logged out')
            }}
          >
            Logout
          </button>
        </div>
      )}
    </GoogleOAuthProvider>
  )
}

export default GoogleAuth
