import React, { useState, useEffect, useCallback } from 'react';
import { GoogleMap, useLoadScript, TrafficLayer } from '@react-google-maps/api';
import { getWeatherData, getCityCoordinates } from '../services/weather';
import './Map.css';

const Map = ({ city, country, onError }) => {
  // State declarations
  const [weather, setWeather] = useState({
    main: { temp: 'N/A' },
    weather: [{ description: 'N/A' }],
  });
  const [center, setCenter] = useState({
    lat: 34.04924594193164,
    lng: -118.24104309082031,
  });
  const [map, setMap] = useState(null);
  const [showTraffic, setShowTraffic] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  // Google Maps script loading
  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY,
  });

  // Fetch city coordinates and weather data
  useEffect(() => {
    const fetchCityData = async () => {
      try {
        setIsLoading(true);
        setWeather({ main: { temp: 'N/A' }, weather: [{ description: 'Loading...' }] });
  
        const { lat, lon } = await getCityCoordinates(city, country);
        setCenter({ lat, lng: lon });
        
        const weatherData = await getWeatherData(lat, lon);
        setWeather(weatherData);
  
      } catch (error) {
        console.error('Location error:', error);
        
        let message;
        switch(error.message) {
          case 'CITY_NOT_IN_COUNTRY':
            message = `${city} not found in ${country}`;
            break;
          case 'COUNTRY_NOT_FOUND':
            message = `${country} not found`;
            break;
          case 'CITY_NOT_FOUND':
            message = `${city} not found globally`;
            break;
          case 'INVALID_INPUT':
            message = 'Please enter a city or country';
            break;
          default:
            message = 'Location service error';
        }
  
        if (onError) onError({ type: 'error', message });
        
        setWeather({
          error: true,
          main: { temp: 'N/A' },
          weather: [{ description: 'Data unavailable' }]
        });
      } finally {
        setIsLoading(false);
      }
    };
  
    const timer = setTimeout(fetchCityData, 500);
    return () => clearTimeout(timer);
  }, [city, country, onError]);

  // Handle map load
  const onLoad = useCallback((map) => {
    setMap(map);
  }, []);

  // Handle errors and loading states
  if (loadError) return <div className="error-message">Error loading maps</div>;
  if (!isLoaded) return <div className="loading-message">Loading Maps...</div>;

  return (
    <div className="map-page-container">
      {/* Map Container (Left Side) */}
      <div className="map-container">
        <GoogleMap
          mapContainerStyle={{ width: '100%', height: '100%' }}
          zoom={13}
          center={center}
          onLoad={onLoad}
          options={{
            disableDefaultUI: true,
            zoomControl: true,
          }}
        >
          {showTraffic && <TrafficLayer />}
        </GoogleMap>
        
        {/* Traffic Toggle Button */}
        <button
          onClick={() => setShowTraffic(!showTraffic)}
          className="traffic-toggle"
          disabled={isLoading}
        >
          {isLoading ? (
            <span className="loading-spinner"></span>
          ) : (
            showTraffic ? 'Hide Traffic' : 'Show Traffic'
          )}
        </button>
      </div>

      {/* Weather Widget (Right Side) */}
      <div className="weather-widget">
        <h2>Weather</h2>
        {isLoading ? (
          <div className="weather-loading">Loading...</div>
        ) : (
          <>
            <p className="temperature">
              {weather.main?.temp || 'N/A'}Â°C
            </p>
            <p className="description">
              {weather.weather?.[0]?.description || 'Data unavailable'}
            </p>
          </>
        )}
      </div>
    </div>
  );
};

export default Map;
