import React, { useState, useEffect, useRef } from 'react';
import Map from './components/Map';
import './App.css';
import axios from 'axios';

function App() {
  const [location, setLocation] = useState({
    city: 'New Delhi',
    country: 'India'
  });
  const [searchQuery, setSearchQuery] = useState({
    city: '',
    country: ''
  });
  const [notification, setNotification] = useState(null);
  const [lastValidSearch, setLastValidSearch] = useState({
    city: 'New Delhi',
    country: 'India'
  });
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userData, setUserData] = useState(null);
  const prevSearchRef = useRef('');
  const mapRef = useRef();

  // Initialize auth state and axios headers
  useEffect(() => {
    const initializeAuth = () => {
      const token = localStorage.getItem('token');
      const storedUserData = localStorage.getItem('userData');

      if (token && storedUserData) {
        try {
          const parsedUserData = JSON.parse(storedUserData);
          setIsAuthenticated(true);
          setUserData(parsedUserData);
          axios.defaults.headers.common['Authorization'] = `Token ${token}`;
        } catch (error) {
          console.error('Failed to parse user data:', error);
          clearAuthState();
        }
      }
    };

    const handleUnauthorized = () => {
      clearAuthState();
      setNotification({
        type: 'error',
        message: 'Session expired. Please login again.'
      });
    };

    window.addEventListener('unauthorized', handleUnauthorized);
    initializeAuth();

    return () => {
      window.removeEventListener('unauthorized', handleUnauthorized);
    };
  }, []);

  const clearAuthState = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('userData');
    setIsAuthenticated(false);
    setUserData(null);
    delete axios.defaults.headers.common['Authorization'];
  };

  // Auto-dismiss notification
  useEffect(() => {
    let timer;
    if (notification) {
      timer = setTimeout(() => {
        setNotification(null);
      }, 2000);
    }
    return () => clearTimeout(timer);
  }, [notification]);

  const handleLoginSuccess = async (credentials) => {
    try {
      const response = await login(credentials);  // Uses the fixed api.js function

      // Update global state
      setIsAuthenticated(true);
      setUserData(response.user);

      // Optional: Redirect or show success message
      setNotification({
        type: 'success',
        message: 'Login successful!'
      });
    } catch (error) {
      setNotification({
        type: 'error',
        message: error.message
      });
    }
  };

  const handleLogout = async () => {
    try {
      const token = localStorage.getItem('token');
      if (token) {
        await axios.post('http://localhost:8000/api/auth/logout/', null, {
          headers: {
            'Authorization': `Token ${token}`
          }
        });
      }
    } catch (error) {
      console.error('Logout error:', error);
      setNotification({
        type: 'error',
        message: 'Logout failed. Please try again.'
      });
    } finally {
      clearAuthState();
      setNotification({
        type: 'info',
        message: 'Logged out successfully'
      });
    }
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    try {
      if (!searchQuery.city && !searchQuery.country) {
        throw new Error('Please enter a city or country');
      }

      const newLocation = {
        city: searchQuery.city.trim(),
        country: searchQuery.country.trim()
      };

      setLocation(newLocation);
      setLastValidSearch(newLocation);
      setSearchQuery({ city: '', country: '' });

      setNotification({
        type: 'info',
        message: `Searching for ${newLocation.city || newLocation.country}...`
      });
    } catch (error) {
      setNotification({
        type: 'error',
        message: error.message
      });
    }
  };

  const handleMapResult = (result) => {
    setNotification({
      type: result.success ? 'success' : 'error',
      message: result.message
    });
    if (result.mapRef) {
      mapRef.current = result.mapRef;
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setSearchQuery(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const refocusOnLastSearch = () => {
    if (mapRef.current && lastValidSearch) {
      mapRef.current.refocus();
      setNotification({
        type: 'info',
        message: `Refocused on ${lastValidSearch.city || lastValidSearch.country}`
      });
    }
  };

  return (
    <div className="App">
      <header className="app-header">
        <h1>Smart City Dashboard</h1>
        {isAuthenticated && (
          <div className="user-info">
            <span>Welcome, {userData?.name || userData?.username}</span>
          </div>
        )}
      </header>

      {notification && (
        <div className={`notification-banner ${notification.type}`}>
          <span>{notification.message}</span>
        </div>
      )}

      <form onSubmit={handleSearch} className="search-bar">
        <div className="search-container">
          <div className="search-controls">
            <button 
              type="button"
              className="refocus-button"
              onClick={refocusOnLastSearch}
              title="Refocus on last searched location"
              aria-label="Refocus map"
            >
              <span className="refocus-icon">üìç</span>
            </button>
            <input
              type="text"
              name="city"
              placeholder="City (e.g. Delhi)"
              value={searchQuery.city}
              onChange={handleInputChange}
              aria-label="Search city"
            />
            <input
              type="text"
              name="country"
              placeholder="Country (e.g. India)"
              value={searchQuery.country}
              onChange={handleInputChange}
              aria-label="Search country"
            />
            <button 
              type="submit" 
              className="search-button"
              disabled={!searchQuery.city && !searchQuery.country}
            >
              Search
            </button>
          </div>
        </div>
      </form>

      <Map 
        city={location.city}
        country={location.country}
        onResult={handleMapResult}
        isAuthenticated={isAuthenticated}
        setIsAuthenticated={setIsAuthenticated}
        ref={mapRef}
        userData={userData}
        setUserData={setUserData}
        onLoginSuccess={handleLoginSuccess}
        onLogout={handleLogout}
      />
    </div>
  );
}

export default App;
