import React, { useState, useEffect, useCallback, useRef } from 'react';
import { GoogleMap, useLoadScript, TrafficLayer } from '@react-google-maps/api';
import { getWeatherData, getCityCoordinates } from '../services/weather';
import { GOOGLE_MAPS_CONFIG } from './configs/maps';
import './Map.css';

const Map = ({ city, country, onResult }) => {
  const [weather, setWeather] = useState(null);
  const [center, setCenter] = useState({ lat: 28.6139, lng: 77.2090 });
  const [map, setMap] = useState(null);
  const [showTraffic, setShowTraffic] = useState(false);
  const [showWeather, setShowWeather] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [mapPadding, setMapPadding] = useState({ right: 0 });
  const prevSearchRef = useRef('');
  const mapRef = useRef(null);

  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: GOOGLE_MAPS_CONFIG.apiKey,
    libraries: GOOGLE_MAPS_CONFIG.libraries,
  });

  const fetchData = useCallback(async () => {
    try {
      const currentSearch = `${city}|${country}`;
      if (prevSearchRef.current === currentSearch || (!city && !country)) {
        return;
      }
      prevSearchRef.current = currentSearch;

      setIsLoading(true);
      setWeather(null);

      const { lat, lon } = await getCityCoordinates(city, country);
      setCenter({ lat, lng: lon });

      const weatherData = await getWeatherData(lat, lon);
      setWeather(weatherData);

      if (onResult) {
        const message = city 
          ? `${city} found${country ? ` in ${country}` : ''}`
          : `Showing capital of ${country}`;
        onResult({ success: true, message });
      }

    } catch (error) {
      console.error('Error:', error);
      if (onResult) {
        let message;
        switch(error.message) {
          case 'CITY_NOT_FOUND':
            message = `${city} not found${country ? ` in ${country}` : ''}`;
            break;
          case 'CITY_NOT_IN_COUNTRY':
            message = `${city} not found in ${country}`;
            break;
          case 'COUNTRY_NOT_FOUND':
            message = `${country} not found`;
            break;
          case 'INVALID_COUNTRY':
            message = `Invalid country: ${country}`;
            break;
          default:
            message = 'Failed to load location data';
        }
        onResult({ success: false, message });
      }
    } finally {
      setIsLoading(false);
    }
  }, [city, country, onResult]);

  useEffect(() => {
    if (!isLoaded) return;
    fetchData();
  }, [isLoaded, fetchData]);

  const onLoad = useCallback((map) => {
    mapRef.current = map;
    setMap(map);
  }, []);

  const onUnmount = useCallback(() => {
    mapRef.current = null;
  }, []);

  useEffect(() => {
    if (map) {
      setMapPadding(showWeather ? { right: 300 } : { right: 0 });
      setTimeout(() => {
        window.dispatchEvent(new Event('resize'));
        map.panTo(center);
      }, 300);
    }
  }, [showWeather, map, center]);

  if (loadError) return <div className="map-error">Error loading maps</div>;
  if (!isLoaded) return <div className="map-loading">Loading Maps...</div>;

  return (
    <div className="map-page-container">
      <div className="map-container">
        <GoogleMap
          mapContainerStyle={{ 
            width: '100%', 
            height: '100%',
            paddingRight: `${mapPadding.right}px`,
            transition: 'padding 0.3s ease'
          }}
          zoom={13}
          center={center}
          onLoad={onLoad}
          onUnmount={onUnmount}
          options={{
            gestureHandling: 'greedy',
            disableDefaultUI: true,
            styles: [
              {
                featureType: "poi",
                elementType: "labels",
                stylers: [{ visibility: "off" }]
              }
            ]
          }}
        >
          {showTraffic && <TrafficLayer />}
        </GoogleMap>

        <div className="map-controls">
          <button
            onClick={() => setShowTraffic(!showTraffic)}
            className="control-button traffic"
            disabled={isLoading}
          >
            {showTraffic ? 'üö¶ Hide Traffic' : 'üö¶ Show Traffic'}
          </button>
          
          <button
            onClick={() => setShowWeather(!showWeather)}
            className="control-button weather"
          >
            {showWeather ? '‚òÄÔ∏è Hide Weather' : '‚òÄÔ∏è Show Weather'}
          </button>
        </div>
      </div>

      {showWeather && (
        <div className="weather-sidebar">
          <div className="sidebar-header">
            <h2>
              {city || 'Current Location'}
              <span className="weather-description">
                {weather?.weather?.[0]?.description}
              </span>
            </h2>
            <button 
              onClick={() => setShowWeather(false)}
              className="close-sidebar"
            >
              &times;
            </button>
          </div>

          {weather ? (
            <div className="weather-content">
              <div className="weather-primary">
                <div className="temperature">
                  {Math.round(weather.main.temp)}¬∞C
                  <span>Feels like {Math.round(weather.main.feels_like)}¬∞C</span>
                </div>
                <div className="weather-icon">
                  <img 
                    src={`https://openweathermap.org/img/wn/${weather.weather[0].icon}@4x.png`} 
                    alt={weather.weather[0].description}
                  />
                </div>
              </div>

              <div className="weather-details">
                <div className="detail-card">
                  <span>üíß Humidity</span>
                  <p>{weather.main.humidity}%</p>
                </div>
                <div className="detail-card">
                  <span>üå¨Ô∏è Wind</span>
                  <p>{weather.wind.speed} m/s</p>
                </div>
                <div className="detail-card">
                  <span>üìä Pressure</span>
                  <p>{weather.main.pressure} hPa</p>
                </div>
                <div className="detail-card">
                  <span>üëÅÔ∏è Visibility</span>
                  <p>{(weather.visibility / 1000).toFixed(1)} km</p>
                </div>
              </div>
            </div>
          ) : (
            <div className="weather-loading">
              {isLoading ? 'Loading weather...' : 'No weather data available'}
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default Map;
