// src/components/Map/Sidebar/FeedbackSidebar.js

import React, { useEffect, useState } from 'react';
import FeedbackForm from '../../Feedback/FeedbackForm';
import FeedbackList from '../../Feedback/FeedbackList';
import api from '../../../services/api';

export default function FeedbackSidebar({
  city,
  isActive,
  showFeedbackForm,
  selectedLocation,
  onReportClick,
  onFeedbackSubmit,
  onToggle
}) {
  const [feedbacks, setFeedbacks] = useState([]);
  const [loading, setLoading] = useState(false);
  const [sortBy, setSortBy] = useState('recent'); // 'recent', 'severity', 'type'

  useEffect(() => {
    if (isActive && city) {
      const fetchFeedbacks = async () => {
        try {
          setLoading(true);
          console.log('📍 Fetching feedbacks for city:', city);
          const response = await api.get(`/api/feedback/?city=${encodeURIComponent(city)}`);
          setFeedbacks(response.data);
        } catch (err) {
          console.error('❌ Failed to fetch feedbacks:', err);
        } finally {
          setLoading(false);
        }
      };

      fetchFeedbacks();
    }
  }, [city, isActive]);

  const sortedFeedbacks = [...feedbacks].sort((a, b) => {
    if (sortBy === 'severity') {
      const rank = { high: 3, medium: 2, low: 1 };
      return (rank[b.severity] || 0) - (rank[a.severity] || 0);
    } else if (sortBy === 'type') {
      return a.issue_type.localeCompare(b.issue_type);
    } else {
      return new Date(b.created_at) - new Date(a.created_at); // Most recent
    }
  });

  if (showFeedbackForm) {
    return (
      <FeedbackForm
        onSubmit={onFeedbackSubmit}
        onCancel={() => onToggle('feedback')}
        location={selectedLocation || `${city} (click to select)`}
      />
    );
  }

  return (
    <>
      <button className="report-issue-btn" onClick={onReportClick}>
        Report New Issue
      </button>

      <div className="sort-controls" style={{ margin: '10px 0' }}>
        <label htmlFor="feedback-sort">Sort by: </label>
        <select
          id="feedback-sort"
          value={sortBy}
          onChange={(e) => setSortBy(e.target.value)}
        >
          <option value="recent">Most Recent</option>
          <option value="severity">Severity</option>
          <option value="type">Issue Type</option>
        </select>
      </div>

      {loading ? (
        <p>Loading feedbacks...</p>
      ) : (
        <FeedbackList feedbacks={sortedFeedbacks} city={city} />
      )}
    </>
  );
}
