import React, { useState } from 'react';
import Map from './components/Map';
import './App.css';
import { getCountryCapital } from './src//services/countryData';

function App() {
  const [location, setLocation] = useState({
    city: 'Delhi',
    country: 'India'
  });
  const [searchQuery, setSearchQuery] = useState({
    city: '',
    country: ''
  });
  const [isLoading, setIsLoading] = useState(false);

  const handleSearch = async (e) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      // Case 1: Only country provided → show capital
      if (searchQuery.country && !searchQuery.city) {
        const capital = await getCountryCapital(searchQuery.country);
        setLocation({
          city: capital,
          country: searchQuery.country
        });
      }
      // Case 2: Only city provided → search globally
      else if (searchQuery.city && !searchQuery.country) {
        setLocation({
          city: searchQuery.city,
          country: '' // Let the Map component handle global search
        });
      }
      // Case 3: Both provided → search city in country
      else if (searchQuery.city && searchQuery.country) {
        setLocation({
          city: searchQuery.city,
          country: searchQuery.country
        });
      }
      // Case 4: Nothing provided → show defaults
      else {
        setLocation({
          city: 'Delhi',
          country: 'India'
        });
      }
    } catch (error) {
      alert(`Search failed: ${error.message}`);
    } finally {
      setIsLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setSearchQuery(prev => ({
      ...prev,
      [name]: value
    }));
  };

  return (
    <div className="App">
      <h1>Smart City Dashboard</h1>

      <form onSubmit={handleSearch} className="search-bar">
        <div className="search-fields">
          <input
            type="text"
            name="city"
            placeholder="City (e.g. Paris)"
            value={searchQuery.city}
            onChange={handleInputChange}
          />
          <input
            type="text"
            name="country"
            placeholder="Country (e.g. France)"
            value={searchQuery.country}
            onChange={handleInputChange}
          />
          <button type="submit" disabled={isLoading}>
            {isLoading ? 'Searching...' : 'Search'}
          </button>
        </div>
        <p className="search-hint">
          {!searchQuery.country && searchQuery.city && 
           "• Will search for city globally"}
          {searchQuery.country && !searchQuery.city && 
           "• Will show country's capital"}
        </p>
      </form>

      <Map 
        city={location.city} 
        country={location.country} 
      />
    </div>
  );
}

export default App;
