import React, { useState, useEffect } from 'react';
import { getTrafficData, getWeatherData } from './services/api';
import './App.css';

function App() {
  const [traffic, setTraffic] = useState(null);
  const [weather, setWeather] = useState(null);

  useEffect(() => {
    // Fetch traffic data
    getTrafficData().then(data => setTraffic(data));

    // Fetch weather data
    getWeatherData().then(data => setWeather(data));

  }, []);

  return (
    <div className="App">
      <h1>Smart City Dashboard</h1>

      <div className="widgets">
        <div className="widget">
          <h2>Traffic Status</h2>
          <p>{traffic ? traffic.status : 'Loading traffic data...'}</p>
        </div>

        <div className="widget">
          <h2>Weather</h2>
          <p>{weather ? `${weather.temperature}°C, ${weather.humidity}% humidity` : 'Loading weather data...'}</p>
        </div>

      </div>
    </div>
  );
}

export default App;
