import React, { useState } from 'react';
import './Feedback.css';

const FeedbackForm = ({ onSubmit, location }) => {
  const [formData, setFormData] = useState({
    issueType: 'pothole',
    description: '',
    severity: 'medium',
    anonymous: false
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const feedback = {
      ...formData,
      location: location, // This should be passed from parent (Map component)
      timestamp: new Date().toISOString()
    };
    onSubmit(feedback);
    setFormData({
      issueType: 'pothole',
      description: '',
      severity: 'medium',
      anonymous: false
    });
  };

  return (
    <div className="feedback-form-container">
      <h3>Report an Issue</h3>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Issue Type:</label>
          <select 
            name="issueType" 
            value={formData.issueType} 
            onChange={handleChange}
            required
          >
            <option value="pothole">Pothole</option>
            <option value="streetlight">Damaged Streetlight</option>
            <option value="intersection">Unsafe Intersection</option>
            <option value="garbage">Garbage Accumulation</option>
            <option value="other">Other</option>
          </select>
        </div>

        <div className="form-group">
          <label>Description:</label>
          <textarea 
            name="description" 
            value={formData.description} 
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Severity:</label>
          <select 
            name="severity" 
            value={formData.severity} 
            onChange={handleChange}
          >
            <option value="low">Low</option>
            <option value="medium">Medium</option>
            <option value="high">High</option>
          </select>
        </div>

        <div className="form-group checkbox">
          <label>
            <input 
              type="checkbox" 
              name="anonymous" 
              checked={formData.anonymous} 
              onChange={handleChange}
            />
            Submit Anonymously
          </label>
        </div>

        <button type="submit" className="submit-btn">Submit Report</button>
      </form>
    </div>
  );
};

export default FeedbackForm;
