// src/components/Map/Sidebar/FeedbackSidebar.js

import React, { useEffect, useState } from 'react';
import FeedbackForm from '../../Feedback/FeedbackForm';
import FeedbackList from '../../Feedback/FeedbackList';
import api from '../../../services/api';

export default function FeedbackSidebar({
  city,
  isActive,
  showFeedbackForm,
  selectedLocation,
  onReportClick,
  onFeedbackSubmit,
  onToggle
}) {
  const [feedbacks, setFeedbacks] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (isActive && city) {
      const fetchFeedbacks = async () => {
        try {
          setLoading(true);
          const response = await api.get(`/api/feedback/?city=${encodeURIComponent(city)}`);
          setFeedbacks(response.data);
        } catch (err) {
          console.error('‚ùå Failed to fetch feedbacks:', err);
        } finally {
          setLoading(false);
        }
      };

      fetchFeedbacks();
    }
  }, [city, isActive]);

  if (showFeedbackForm) {
    return selectedLocation ? (
      <FeedbackForm
        key={selectedLocation.name} // üí° this forces remount when name changes
        onSubmit={onFeedbackSubmit}
        onCancel={() => onToggle('feedback')}
        location={selectedLocation}
      />
    ) : (
      <p>üìç Click on the map to select a location.</p>
    );
  }
  
}
