import React from 'react';
import './Feedback.css';

const FeedbackItem = ({ feedback }) => {
  const getIcon = (type) => {
    switch(type) {
      case 'pothole': return '🕳️';
      case 'streetlight': return '💡';
      case 'intersection': return '🚦';
      case 'garbage': return '🗑️';
      default: return '❗';
    }
  };

  const getSeverityColor = (severity) => {
    switch(severity) {
      case 'low': return 'green';
      case 'medium': return 'orange';
      case 'high': return 'red';
      default: return 'gray';
    }
  };

  return (
    <div className="feedback-item">
      <div className="feedback-header">
        <span className="feedback-icon">{getIcon(feedback.issueType)}</span>
        <span className="feedback-type">{feedback.issueType}</span>
        <span 
          className="feedback-severity" 
          style={{ backgroundColor: getSeverityColor(feedback.severity) }}
        >
          {feedback.severity}
        </span>
      </div>
      <div className="feedback-description">{feedback.description}</div>
      <div className="feedback-footer">
        <span className="feedback-location">{feedback.location}</span>
        <span className="feedback-date">
          {new Date(feedback.timestamp).toLocaleString()}
        </span>
      </div>
    </div>
  );
};

export default FeedbackItem;
