// src/components/Map/ControlPanel.js

import React from 'react'
import './css/LeftPanel.css'

const controls = [
  { name: 'weather',   icon: '‚òÄÔ∏è', label: 'Weather' },
  { name: 'airQuality',icon: 'üå´Ô∏è', label: 'Air Quality' },
  { name: 'traffic',   icon: 'üö¶', label: 'Traffic' },
  { name: 'feedback',  icon: 'üìù', label: 'Reports' },
  { name: 'alerts',    icon: 'üö®', label: 'Alerts' },
  { name: 'zones',     icon: 'üöß', label: 'Accident Zones' },
]

export default function ControlPanel({ active, onToggle }) {
  return (
    <div className="left-panel">
      <div className="panel-title">Menu</div>
      {controls.map(c => (
        <div
          key={c.name}
          className={`control-card ${active === c.name ? 'active' : ''}`}
          onClick={() => onToggle(c.name)}
        >
          <div className="control-icon">{c.icon}</div>
          <div className="control-label">{c.label}</div>
        </div>
      ))}
    </div>
  )
}
