import React, { useState, useEffect, useCallback } from 'react';
import { GoogleMap, useLoadScript, TrafficLayer } from '@react-google-maps/api';
import { getWeatherData, getCityCoordinates } from '../services/weather';
import './Map.css';

const Map = ({ city, country, onError }) => {
  // State declarations
  const [weather, setWeather] = useState({
    main: { temp: 'N/A' },
    weather: [{ description: 'N/A' }],
  });
  const [center, setCenter] = useState({
    lat: 28.6139,  // Default to New Delhi coordinates
    lng: 77.2090,
  });
  const [map, setMap] = useState(null);
  const [showTraffic, setShowTraffic] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  // Google Maps script loading
  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY,
    libraries: ['places'],
  });

  // Fetch city coordinates and weather data
  useEffect(() => {
    const fetchCityData = async () => {
      try {
        setIsLoading(true);
        setWeather({
          main: { temp: 'N/A' },
          weather: [{ description: 'Loading...' }],
        });

        // Skip if no city provided (country-only searches handled by getCityCoordinates)
        if (!city && !country) return;

        const { lat, lon } = await getCityCoordinates(city, country);
        setCenter({ lat, lng: lon });

        const weatherData = await getWeatherData(lat, lon);
        setWeather(weatherData);

        // Show success notification for country capital searches
        if (!city && country && onError) {
          onError({
            type: 'success',
            message: `Showing capital of ${country}`,
            persist: false,
          });
        }

      } catch (error) {
        console.error('Location error:', error);
        
        let message;
        switch(error.message) {
          case 'CITY_NOT_IN_COUNTRY':
            message = `${city} not found in ${country}`;
            break;
          case 'COUNTRY_NOT_RECOGNIZED':
            message = `"${country}" not recognized. Try country codes (e.g., IN for India)`;
            break;
          case 'CITY_NOT_FOUND':
            message = `${city} not found globally`;
            break;
          case 'COUNTRY_NOT_FOUND':
            message = `${country} not found`;
            break;
          default:
            message = 'Failed to load location data';
        }

        if (onError) {
          onError({ 
            type: 'error', 
            message,
            persist: true,
          });
        }

        setWeather({
          error: true,
          main: { temp: 'N/A' },
          weather: [{ description: 'Location not found' }],
        });
      } finally {
        setIsLoading(false);
      }
    };

    const timer = setTimeout(() => {
      fetchCityData();
    }, 500);

    return () => clearTimeout(timer);
  }, [city, country, onError]);

  // Handle map load
  const onLoad = useCallback((map) => {
    setMap(map);
  }, []);

  // Handle errors and loading states
  if (loadError) {
    return (
      <div className="map-error">
        <h3>Error loading Google Maps</h3>
        <p>Please try refreshing the page or check your internet connection.</p>
      </div>
    );
  }

  if (!isLoaded) {
    return (
      <div className="map-loading">
        <div className="spinner"></div>
        <p>Loading Maps...</p>
      </div>
    );
  }

  return (
    <div className="map-page-container">
      {/* Map Container (Left Side) */}
      <div className="map-container">
        <GoogleMap
          mapContainerStyle={{ width: '100%', height: '100%' }}
          zoom={13}
          center={center}
          onLoad={onLoad}
          options={{
            disableDefaultUI: true,
            zoomControl: true,
            streetViewControl: true,
            mapTypeControl: true,
          }}
        >
          {showTraffic && <TrafficLayer />}
        </GoogleMap>
        
        {/* Traffic Toggle Button */}
        <button
          onClick={() => setShowTraffic(!showTraffic)}
          className="traffic-toggle"
          disabled={isLoading}
          aria-label={showTraffic ? 'Hide traffic' : 'Show traffic'}
        >
          {isLoading ? (
            <span className="loading-spinner"></span>
          ) : (
            showTraffic ? 'Hide Traffic' : 'Show Traffic'
          )}
        </button>
      </div>

      {/* Weather Widget (Right Side) */}
      <div className="weather-widget">
        <h2>Weather</h2>
        {isLoading ? (
          <div className="weather-loading">
            <div className="spinner small"></div>
            <p>Loading weather...</p>
          </div>
        ) : (
          <>
            <p className="temperature">
              {weather.main?.temp || 'N/A'}Â°C
            </p>
            <p className="description">
              {weather.weather?.[0]?.description || 'Data unavailable'}
            </p>
            {weather.main?.humidity && (
              <p className="humidity">
                Humidity: {weather.main.humidity}%
              </p>
            )}
          </>
        )}
      </div>
    </div>
  );
};

export default Map;
